mapper.insert:
UPDATE TB_ORD
   SET ORD_STAT_CD = #{ORD_STAT_CD}
   ,   ITEM_CD = #{ITEM_CD}
 WHERE ORD_NO = #{ORD_NO}

            INSERT INTO TB_CUST(
                  CUST_GBCD
            	, CUST_NM
            	, PHONE
            	, BIRTHBISNO
            	, ADDR
            	, REG_DT
            	, UPD_DT
            ) VALUES(
				  #{CUST_GBCD}
            	, #{CUST_NM}
            	, #{PHONE}
            	, #{BIR_BIZ_NO}
            	, #{ADDR}
            	, now()
            	, now()
            )

INSERT INTO TB_ORD(          	
	  ITEM_CD
	, CUST_CD
	, ORD_STAT_CD
	, REG_DT
	, UPD_DT
) VALUES(           	
	  #{ITEM_CD}
	, #{CUST_CD}
	, 'A'   /*최초 주문등록 시에는 주문대기 상태로 지정*/
	, now()
	, now()
)

mapper.select:
          SELECT CUST_CD
			  FROM TB_CUST
			 WHERE CUST_GBCD = #{CUST_GBCD}
			   AND CUST_NM = #{CUST_NM}
			   AND PHONE = #{PHONE}
			   AND BIRTHBISNO = #{BIR_BIZ_NO}
			   AND ADDR = #{ADDR}

SELECT ORD.ORD_NO AS ORD_NO , MST.CD_NM1 AS ORD_STAT_NM , ORD.CUST_CD AS CUST_NO , CUST.CUST_NM AS CUST_NM , CUST.CUST_GBCD AS CUST_GBCD_NM , CUST.PHONE AS PHONE , CUST.ADDR AS ADDR , ITEM.ITEM_NM AS ITEM_NM , ORD.REG_DT AS REG_DT FROM TB_ORD ORD LEFT JOIN TB_CUST CUST ON ORD.CUST_CD = CUST.CUST_CD LEFT JOIN TB_ITEM ITEM ON ORD.ITEM_CD = ITEM.ITEM_CD INNER JOIN TB_CD_MST MST ON ORD.ORD_STAT_CD = MST.CD_VAL1 AND MST.CD_VAL = '001' WHERE 1=1
AND ORD.ORD_NO = #{ORD_NO}
AND CUST.CUST_NM = #{CUST_NM}
AND CUST.CUST_GBCD = 'C'
AND ORD.ORD_STAT_CD = #{ORD_STAT_CD}
AND CUST.CUST_GBCD = #{CUST_GBCD}

mapper.update:
update tbl_board 
   set title       = #{title}
     , regId       = #{regId}
     , contents    = #{contents}
     , communityId = #{communityId}
where postId=#{postId}

update tbl_board 
